import {
  faChevronLeft,
  faChevronRight,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Card, CardMedia, Container, Stack, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { Carousel } from "@trendyol-js/react-carousel";
import React from "react";
import Map from "../components/Map";

const About = () => {
  return (
    <Container maxWidth="1024">
      <Carousel
        leftArrow={
          <Box
            style={{
              display: "flex",
              alignItems: "center",
              height: "100%",
            }}
          >
            <FontAwesomeIcon icon={faChevronLeft} />
          </Box>
        }
        show={3}
        slide={1}
        swiping={true}
        rightArrow={
          <Box
            style={{ display: "flex", alignItems: "center", height: "100%" }}
          >
            <FontAwesomeIcon icon={faChevronRight} />
          </Box>
        }
      >
        <Box display="flex" justifyContent="center">
          <img
            alt="map"
            src={require("../image/ppssalogan.jpg")}
            style={{
              borderRadius: "15px",
              width: "60%",
              height: "auto",
            }}
          />
          <img
            alt="map"
            src={require("../image/freeservice.jpg")}
            style={{
              borderRadius: "15px",
              width: "60%",
              height: "auto",
            }}
          />
          <img
            alt="map"
            src={require("../image/mappps.jpg")}
            style={{
              borderRadius: "15px",
              width: "60%",
              height: "auto",
            }}
          />
        </Box>
      </Carousel>
      <Typography variant="h5" textAlign="center" marginBottom="60px">
        ประเภทน้ำดื่มของทางเพิ่มพูนทรัพย์
      </Typography>
      {/* <Box>ขวดขนาดต่างๆ</Box> */}
      <hr
        style={{
          color: "rgb(116, 114, 114)",
          backgroundColor: "rgb(116, 114, 114)",
          height: 0.5,
          borderColor: "rgb(116, 114, 114)",
          width: "1000px",
          marginBottom: "60px",
        }}
      />

      <Typography variant="h5" textAlign="center" marginBottom="60px">
        ขั้นตอนการผลิตน้ำ
      </Typography>
      <Stack
        justifyContent="center"
        flexDirection="row"
        gap={5}
        marginBottom="60px"
      >
        {/* <Box
          sx={{
            backgroundColor: "#fff",
            borderRadius: "50%",
            width: "150px",
            height: "150px",
          }}
        >
          ro
        </Box> */}
        <img
          alt="map"
          src={require("../image/ro.jpg")}
          style={{
            borderRadius: "50%",
            width: "150px",
            height: "150px",
            height: "auto",
          }}
        />
        <img
          alt="map"
          src={require("../image/uv.jpg")}
          style={{
            borderRadius: "50%",
            width: "150px",
            height: "150px",
            height: "auto",
          }}
        />
      </Stack>

      <Typography textAlign="center" marginBottom="60px">
