import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronLeft,
  faChevronRight,
} from "@fortawesome/free-solid-svg-icons";
import { Card, CardMedia, Container, Stack, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { Carousel } from "@trendyol-js/react-carousel";
import React from "react";
import Map from "../components/Map";
import ppssalogan from "../image/ppssalogan.jpg";

const About = () => {
  return (
    <Container>
      <Box
        marginBottom={4}
        display="flex"
        justifyContent="center"
        maxWidth="1200px"
      >
        <Carousel
          leftArrow={
            <Box
              style={{
                display: "flex",
                alignItems: "center",
                height: "100%",
              }}
            >
              <FontAwesomeIcon icon={faChevronLeft} />
            </Box>
          }
          show={1}
          slide={1}
          swiping={true}
          rightArrow={
            <Box
              style={{ display: "flex", alignItems: "center", height: "100%" }}
            >
              <FontAwesomeIcon icon={faChevronRight} />
            </Box>
          }
        >
          <img
            alt="map"
            style={{ width: "1136px", height: "auto" }}
            src={require("../image/ppssalogan.jpg")}
          />
          <img
            alt="map"
            style={{ width: "1136px", height: "auto" }}
            src={require("../image/freeservice.jpg")}
          />
          <img
            alt="map"
            style={{ width: "1136px", height: "auto" }}
            src={require("../image/produce.jpg")}
          />
        </Carousel>
      </Box>
      <Container>
        <Typography variant="h5" textAlign="center" marginBottom={2}>
          ช่องทางการติดต่อ(Contact)
        </Typography>

        <Typography variant="h5" marginBottom={4} color="#fff" fontWeight={600}>
          LINE PPS Water
        </Typography>
        <img
